package InitVariable;

public class ScannerLuncher {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner.setcolour("black");
		Scanner.setsizeIncm(150);
		Scanner.setcostInRs(5000);
		System.out.println("Colour is :" + Scanner.colour +"\n"+ "Size In Cm is :" + Scanner.size +"\n"+ "Cost in INR Rs :" +Scanner.cost );
	}

}
